mapper.select:
SELECT board_no, board_category ,DECODE(board_blind, 'Y', '블라인드 처리된 게시글입니다.', board_title) AS board_title ,board_content, member_name, board_written_date, board_hit ,board_like, board_dislike, board_report_count, board_blind FROM tb_community NATURAL JOIN tb_member
AND board_no = ${board_no}
AND board_category = ${board_category}
ORDER BY board_no DESC

mapper.update:
		UPDATE tb_community
           SET board_blind = #{board_blind}
		 WHERE board_no = #{board_no}

UPDATE tb_community
   SET board_category = #{board_category}
   	  ,board_title = #{board_title}
   	  ,board_content = #{board_content}
 WHERE board_no = #{board_no}
